import React from 'react'
import { useEffect,useState } from 'react'
import { ReactComponent as ArrowLeft } from '../assets/arrow-left.svg'
import { useParams, useNavigate } from "react-router-dom";



function Notepage( history ) {

    const { noteId } = useParams();

    const navigate = useNavigate();

    let [note,setNote] = useState(null)

    
    useEffect( () => {
      getNote()
      

    },[noteId])
    

    let getNote = async ()=> {
      if (noteId === 'new') return

      let response = await fetch(`/api/note/${noteId}/`)

      let data = await response.json()

      setNote(data)

    }

    let updateNote= async ()=>{

      fetch(`/api/note/${noteId}/update/`,{
        method : 'PUT',
        headers : {
          'Content-Type' : 'application/json'
        },
        body : JSON.stringify(note)


      })


    }

    let handleSubmit = ()=>{
      if(noteId === "new" && note === null) {
    
        navigate('/')
        return
      }
      
      if( noteId !== "new" && note.body === ''){
        deleteNote()
      }
      else if( noteId !== "new" ){
        updateNote()
      }
      else if( noteId === "new" && note.body !== null){
    
        createNote()
      }
      
      navigate('/')

    }

    let deleteNote = ()=>{
      fetch(`/api/note/${noteId}/delete/`,{
        method : 'DELETE',
        headers : {
        'Content-Type' : 'application/json'
        }

      })
      navigate('/');

    }

    let createNote = async ()=>{

      fetch(`/api/note/create/`,{
        method : 'POST',
        headers : {
          'Content-Type' : 'application/json'
        },
        body : JSON.stringify(note)

      })


    }

    let handleChange = (value)=>{
      setNote(note => ({...note,'body':value}))

    }
    

   
  return (
    <div className="note" >
            <div className="note-header">
                <h3>
                    <ArrowLeft onClick={handleSubmit}/> 
                </h3>
                { noteId !== "new" ? ( <button onClick={deleteNote}>Delete</button> ):
                  ( <button onClick={handleSubmit}>Done</button> )
                }
                
                

            </div>
            <textarea onChange={(e)=>{ handleChange(e.target.value)} } value={note?.body}></textarea>
        </div>
  )
}

export default Notepage
